app.controller('DriverEditController', ['$scope', '$location', '$routeParams', '$q', 'DriverService', 'PassengerService', 'PassengerService', 'PassengerService',
    function ($scope, $location, $routeParams, $q, DriverService , PassengerService , PassengerService, PassengerService) {

    	//manage create and save
		$scope.external = {};
		
    	if ($routeParams.id != 'new')
    	{
        	$scope.id = $routeParams.id;
        	$scope.obj = DriverService.get({_id: $scope.id});
        	$scope.external._Passenger_drivers = PassengerService.findBy_drivers({key: $scope.id});
        	
    	}
    	else{
    		$scope.obj = new DriverService();
        	$scope.external._Passenger_drivers = [];
        	
    	}
    	
    	$scope.save = function(){
    		$scope.obj.$save().then(function(data){
    			$scope.obj=data;
        		$location.path('/drivers/');
    		});
    	}
    	
    	$scope.remove = function(){
    		DriverService.remove({_id: $scope.obj._id}).$promise.then(function(){
				$('#removeModal').modal('hide');
				$('.modal-backdrop').remove();
				$('.modal-open').removeClass("modal-open");
        		$location.path('/drivers/');
    		});
    	}
    	
    		
        //manage relation _passenger
        		
    	$scope.list_Passenger = PassengerService.query();

    		
        //manage External relation _drivers
        		
    	$scope.list_Passenger_drivers = PassengerService.query();
    	
}]);